<script>
    document.addEventListener(""DOMContentLoaded"", function () {
        const elements = document.querySelectorAll('.animated-element');  // حدد جميع العناصر التي تحتاج للحركة عند التمرير

        const observer = new IntersectionObserver((entries, observer) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('visible');  // أضف الكلاس 'visible' عند رؤية العنصر
                    observer.unobserve(entry.target);  // توقف عن مراقبة العنصر بعد ظهوره
                }
            });
        }, { threshold: 0.5 });  // يظهر التأثير عندما يظهر 50% من العنصر داخل نافذة العرض

        // مراقبة جميع العناصر
        elements.forEach(element => {
            observer.observe(element);
        });
    });


</script>












